from typing import TypedDict, List



# Discovery
# The discovery contains all the prediction information of the 
# model based on the test dataset.
class IDiscovery(TypedDict):
    # Predictions generated by the model
    neutral_num: int
    increase_num: int
    decrease_num: int

    # Outcomes during the discovery
    neutral_outcome_num: int
    increase_outcome_num: int
    decrease_outcome_num: int

    # The points collected during the discovery
    points_hist: List[float]
    points: float

    # The accuracy of the generated predictions
    increase_accuracy: float
    decrease_accuracy: float
    accuracy: float

    # Details of the increase predictions
    increase_list: List[float]
    increase_min: float
    increase_max: float
    increase_mean: float
    increase_median: float

    # Details of the decrease predictions
    decrease_list: List[float]
    decrease_min: float
    decrease_max: float
    decrease_mean: float
    decrease_median: float

    # Details of the successful increase predictions
    increase_successful_list: List[float]
    increase_successful_mean: float
    increase_successful_median: float

    # Details of the unsuccessful increase predictions
    increase_unsuccessful_list: List[float]
    increase_unsuccessful_mean: float
    increase_unsuccessful_median: float

    # Details of the successful decrease predictions
    decrease_successful_list: List[float]
    decrease_successful_mean: float
    decrease_successful_median: float

    # Details of the unsuccessful decrease predictions
    decrease_unsuccessful_list: List[float]
    decrease_unsuccessful_mean: float
    decrease_unsuccessful_median: float







